const RunningBanner = () => {
  const bannerText = "ðŸš€ This Portfolio is Deployed on AWS EKS Kubernetes Cluster â€¢ Showcasing Container Orchestration & Cloud-Native Architecture â€¢ Infrastructure as Code with Terraform â€¢ Automated CI/CD with GitHub Actions â€¢";
  const gap = "        "; // Creates visual gap between repeating text

  return (
    <div className="running-banner">
      <div className="banner-content">
        <span className="banner-text">
          {bannerText}{gap}{bannerText}{gap}
        </span>
      </div>
    </div>
  );
};

export default RunningBanner;
