import React from 'react';

const Projects = () => {
  const projects = [
    {
      title: "AWS Cost Optimization Platform",
      description: "Implemented comprehensive AWS cost optimization strategies using automated resource tagging, rightsizing recommendations, and scheduled shutdown policies, resulting in 40% cost reduction.",
      technologies: ["Terraform", "AWS Lambda", "CloudWatch", "Python", "Cost Explorer API"],
      github: "#",
      demo: "#"
    },
    {
      title: "Kubernetes Multi-Environment Platform",
      description: "Designed and deployed production-ready Kubernetes clusters on EKS with automated CI/CD pipelines, monitoring, and security policies using GitOps methodology.",
      technologies: ["Kubernetes", "EKS", "ArgoCD", "Helm", "GitHub Actions", "Dynatrace"],
      github: "#",
      demo: "#"
    },
    {
      title: "Infrastructure as Code Framework",
      description: "Built reusable Terraform modules for AWS infrastructure provisioning with automated testing, compliance checks, and multi-environment deployment strategies.",
      technologies: ["Terraform", "CloudFormation", "GitHub Actions", "Terratest", "AWS Config"],
      github: "#",
      demo: "#"
    },
    {
      title: "Dynatrace Monitoring Automation",
      description: "Developed automated Dynatrace deployment and configuration management using Infrastructure as Code, including custom dashboards and alerting rules.",
      technologies: ["Dynatrace", "Terraform", "Python", "REST APIs", "Monaco Tool"],
      github: "#",
      demo: "#"
    },
    {
      title: "Database Performance Analytics",
      description: "Created Tableau dashboards for database performance monitoring and optimization, integrating with AWS RDS CloudWatch metrics and custom application logs.",
      technologies: ["Tableau", "AWS RDS", "PostgreSQL", "CloudWatch", "Python", "SQL"],
      github: "#",
      demo: "#"
    },
    {
      title: "Automated Backup & Disaster Recovery",
      description: "Implemented automated backup solutions with cross-region replication and disaster recovery testing for critical database systems and applications.",
      technologies: ["AWS Backup", "Lambda", "CloudFormation", "S3", "Cross-Region Replication"],
      github: "#",
      demo: "#"
    }
  ];

  return (
    <section id="projects" className="projects">
      <div className="container">
        <h2>Infrastructure Projects</h2>
        <div className="projects-grid">
          {projects.map((project, index) => (
            <div key={index} className="project-card">
              <h3>{project.title}</h3>
              <p className="project-description">{project.description}</p>
              <div className="project-technologies">
                {project.technologies.map((tech, techIndex) => (
                  <span key={techIndex} className="tech-tag">{tech}</span>
                ))}
              </div>
              <div className="project-links">
                <a href={project.github} className="project-link">GitHub</a>
                <a href={project.demo} className="project-link">Documentation</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
